function formatDate(date) {
    var d = new Date(date),
        month = '' + (d.getMonth() + 1),
        day = '' + d.getDate(),
        year = d.getFullYear();
        hour = '' + d.getHours();
        mins = '' + d.getMinutes();
        secs = '' + d.getSeconds();

    if (month.length < 2) 
        month = '0' + month;
    if (day.length < 2) 
        day = '0' + day;
    if (hour.length < 2) 
        hour = '0' + hour;
    if (mins.length < 2) 
        mins = '0' + mins;
    if (secs.length < 2) 
        secs = '0' + secs;

    return ([day, month, year ].join('-'))+(' ')+([hour, mins, secs].join(':'));
}

